-- ----------------------------
-- Procedure structure for lp_common_power_action
-- ----------------------------
DROP PROCEDURE IF EXISTS `lp_common_power_action`;
delimiter ;;
CREATE PROCEDURE `lp_common_power_action`(p_user_account VARCHAR(60), p_common_power VARCHAR(500))
LABEL_PROC:
BEGIN 
  # check parameter
  SELECT user_power, common_power INTO @user_power, @common_power FROM user_info WHERE user_account = p_user_account;
  IF FOUND_ROWS() = 0 THEN
    SELECT -2502021 AS return_code, 'user_account not found' AS return_msg;
    LEAVE LABEL_PROC; 
  END IF; 
  
  
  IF p_common_power <> '' THEN
    SET @common_power = p_common_power;
  END IF; 
  SET @new_common_power = '';
  SET @idx = 1;
  WHILE (@idx <= 500) DO
  
    SET @tmp_power = '0';
  
    IF SUBSTRING(@user_power, @idx, 1) = '1' AND SUBSTRING(@common_power, @idx, 1) = '1' THEN
      SET @tmp_power = '1'; 
    END IF;
  
    SET @new_common_power = CONCAT(@new_common_power, @tmp_power);
    SET @idx = @idx + 1;
  END WHILE;
  # update common power
  UPDATE user_info SET common_power = @new_common_power WHERE user_account = p_user_account;
END LABEL_PROC
;;
delimiter ;

