CREATE PROCEDURE `lp_game_version_info_add`(p_game_version_id VARCHAR(60), p_partner_id VARCHAR(60), 
    p_game_version_name VARCHAR(60) CHARACTER SET utf8, p_game_version_state VARCHAR(1), p_memo VARCHAR(500) CHARACTER SET utf8, 
