LABEL_PROC:
BEGIN
  # check parameter
  IF p_game_version_id = '' THEN
    SELECT -2523001 AS return_code, 'game_version_id error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  IF LOCATE(',', p_game_version_id) > 0 OR LOCATE(',#%', p_game_version_id) > 0 THEN
    SELECT -2523001 AS return_code, CONCAT('game_version_id error, game_version_id: ', p_game_version_id) AS return_msg;
