  END IF;
  IF p_game_version_name = '' THEN
    SELECT -2523021 AS return_code, 'game_version_name error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  IF p_game_version_state = '' THEN
    SELECT -2523022 AS return_code, 'game_version_state error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  IF p_memo = '' THEN
    SELECT -2523023 AS return_code, 'memo error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  IF p_audit_mode < 0 THEN
    SELECT -2523026 AS return_code, 'audit_mode error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  IF p_update_flag < 0 THEN
    SELECT -2523025 AS return_code, 'update_flag error' AS return_msg;
