  IF EXISTS (SELECT 1 FROM game_version_info WHERE partner_id = p_partner_id AND (game_version_id = p_game_version_id OR game_version_name = p_game_version_name)) THEN
    SELECT -2523041 AS return_code, 'table game_version_info row repeat' AS return_msg;
