DROP PROCEDURE IF EXISTS `lp_gm_gift_code_create`;
delimiter ;;
CREATE PROCEDURE `lp_gm_gift_code_create`(p_oss_gift_flow INT, p_oss_gift_code INT, p_partner_id VARCHAR(60), p_gift_cnt INT, 
            p_item_id VARCHAR(5000), p_gift_info VARCHAR(500) CHARSET utf8, p_expire_date DATETIME)
LABEL_PROC:
BEGIN
  # check parameter
  IF p_oss_gift_flow <= 0 OR p_oss_gift_flow > 10 THEN
    SELECT -2521021 AS return_code, 'config gift_flow error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  
  IF p_oss_gift_code <= 0 OR p_oss_gift_code > 60 || p_oss_gift_code - p_oss_gift_flow < 4 THEN
    SELECT -2521022 AS return_code, 'config gift_code error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  IF p_gift_cnt <= 0 THEN
    SELECT -2521041 AS return_code, 'gift_cnt error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  
  IF UNIX_TIMESTAMP(p_expire_date) = 0 THEN
    SELECT -2521042 AS return_code, 'expire_date error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  IF p_item_id = '' THEN
    SELECT -2521043 AS return_code, 'is_code_repeat error, if is_code_repeat is Y, gift_cnt must be 1' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  # check partner_info
  IF p_partner_id != '' THEN
    IF NOT EXISTS (SELECT 1 FROM partner_info WHERE partner_id = p_partner_id) THEN
      SELECT -2521063 AS return_code, 'partner_id not found' AS return_msg;
      LEAVE LABEL_PROC;
    END IF;
  END IF;
  #gift_flow
  SET @gift_flow = '';
  SET @i = 0;
  
  WHILE @gift_flow = '' AND @i < 100000 DO
  
    SET @j = 0;
  
    WHILE @j < p_oss_gift_flow DO
      SET @gift_flow = CONCAT(@gift_flow, CHAR(FLOOR(RAND() * 26) + 65));
      SET @j = @j + 1;
    END WHILE;
  
    #SET @gift_flow = CONCAT(CHAR(FLOOR(RAND() * 26) + 65), CHAR(FLOOR(RAND() * 26) + 65), CHAR(FLOOR(RAND() * 26) + 65), CHAR(FLOOR(RAND() * 26) + 65));
    
    IF EXISTS (SELECT 1 FROM gm_gift_code_info WHERE gift_flow = @gift_flow) THEN
      SET @gift_flow = '';
      SET @i = @i + 1;
    END IF;   
  END WHILE;
  # check gift_code_info
  IF @gift_flow = '' THEN
    SELECT -2521001 AS return_code;
    LEAVE LABEL_PROC;
  END IF;
  # insert gift_code_info
  INSERT IGNORE INTO gm_gift_code_info (gift_flow, partner_id, gift_cnt, item_id, gift_info, expire_date, create_date, state)
    VALUES (@gift_flow, p_partner_id, p_gift_cnt, p_item_id, p_gift_info, p_expire_date, NOW(), 'A');
  IF ROW_COUNT() <= 0 THEN
    SELECT -2521061 AS return_code;
    LEAVE LABEL_PROC;
  END IF;
  # create detail
  SET @char_list = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  SET @i = 1;
  SET @tmp_gift_len = p_oss_gift_code - p_oss_gift_flow;
  WHILE @i <= p_gift_cnt DO
  
    SET @gift_code = @gift_flow;
    SET @j = 1;
  
    WHILE @j <= @tmp_gift_len DO
      SET @gift_code = CONCAT(@gift_code, SUBSTRING(@char_list , CEILING(RAND() * 26), 1));
      SET @j = @j + 1;
    END WHILE;
    # intsert gift_code_detail
    INSERT IGNORE INTO gm_gift_code_detail (gift_code, gift_flow, act_server_id, act_partner_id, act_user_account, act_activity_id, update_date)
      VALUES (@gift_code, @gift_flow, '', '', '', '', '0000-00-00 00:00:00');
  
    IF ROW_COUNT() > 0 THEN
      SET @i = @i + 1;
    END IF;
  END WHILE;
  # return 
  SELECT 1 AS return_code, @gift_flow AS gift_flow; 
END LABEL_PROC
;;
delimiter ;

-- ----------------------------
-- Procedure structure for lp_gm_gift_code_use
-- ----------------------------
DROP PROCEDURE IF EXISTS `lp_gm_gift_code_use`;
delimiter ;;
CREATE PROCEDURE `lp_gm_gift_code_use`(p_act_server_id VARCHAR(60), p_act_partner_id VARCHAR(60), p_act_user_account VARCHAR(60), p_act_activity_id VARCHAR(60), p_gift_code VARCHAR(60))
LABEL_PROC:
BEGIN
  # check gm_gift_code_detail
  SELECT gift_flow, act_user_account INTO @gift_flow, @act_user_account 
    FROM gm_gift_code_detail 
    WHERE gift_code = UCASE(p_gift_code);
  IF FOUND_ROWS() = 0 THEN
    SELECT 106 AS return_code, 'gift_code not found' AS return_msg;
    LEAVE LABEL_PROC;   
  END IF; 
  # check character_id
  IF @act_user_account <> '' THEN 
    SELECT 105 AS return_code, 'gift_code is used' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  # check gm_gift_code_info
  SELECT partner_id, item_id, expire_date, state INTO @partner_id, @item_id, @expire_date, @state 
    FROM gm_gift_code_info 
    WHERE gift_flow = @gift_flow;
  IF FOUND_ROWS() = 0 THEN
    SELECT 106 AS return_code, 'gift_flow not found' AS return_msg;
    LEAVE LABEL_PROC;       
  END IF; 
  
  
  # check gift_flow active
  IF @state <> 'A' THEN
    SELECT 104 AS return_code, 'gift_flow not active' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;   
  
  # check gift_flow expire_date
  IF @expire_date < NOW() THEN
    SELECT 107 AS return_code, 'gift_flow expire_date error' AS return_msg;
    LEAVE LABEL_PROC;
  END IF; 
  
  
  # check partner
  IF @partner_id != '' AND @partner_id != p_act_partner_id THEN 
    SELECT 105 AS return_code, 'partner not use' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  # 原来逻辑
  IF EXISTS (SELECT 1 FROM gm_gift_code_detail a
      INNER JOIN gm_gift_code_info b ON a.gift_flow = b.gift_flow
      WHERE a.act_user_account = p_act_user_account AND a.gift_flow = @gift_flow) THEN
    SELECT 113 AS return_code, 'gift_flow is used' AS return_msg;
    LEAVE LABEL_PROC;
  END IF;
  UPDATE gm_gift_code_detail SET 
    act_server_id = p_act_server_id, 
    act_partner_id = p_act_partner_id, 
    act_user_account = p_act_user_account, 
    act_activity_id = p_act_activity_id, 
    update_date = NOW()
    WHERE gift_code = UCASE(p_gift_code);
  # return info
  SELECT 1 AS return_code, '' AS return_msg, @item_id AS item_id;
END LABEL_PROC
;;
delimiter ;

