-- ----------------------------
-- Procedure structure for lp_group_power_set
-- ----------------------------
DROP PROCEDURE IF EXISTS `lp_group_power_set`;
delimiter ;;
CREATE PROCEDURE `lp_group_power_set`(p_group_id INT, p_power_value VARCHAR(500))
LABEL_PROC:
BEGIN 
  DECLARE done BOOLEAN;
  DECLARE user_account VARCHAR(60);
  DECLARE power_value VARCHAR(500);
  DECLARE cur_power CURSOR FOR SELECT u.user_account, u.power_value FROM user_group g, user_info u 
    WHERE g.user_account = u.user_account AND g.group_id = p_group_id;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;
  
  # check parameter
  IF NOT EXISTS(SELECT 1 FROM group_info WHERE group_id = p_group_id) THEN
    SELECT -2502041 AS return_code, 'group_id not found' AS return_msg;
    LEAVE LABEL_PROC;       
  END IF; 
  
  # update group power value
  UPDATE group_info SET power_value = p_power_value WHERE group_id = p_group_id;
  
  SET done = 0;
  OPEN cur_power;
  FETCH cur_power INTO user_account, power_value;
  WHILE (done = 0) DO          
    
    CALL lp_user_power_action(user_account, power_value);
    
    FETCH cur_power INTO user_account, power_value;
  END WHILE;
  CLOSE cur_power;
  # return info
  SELECT 1 AS return_code;
  
END LABEL_PROC
;;
delimiter ;

