DROP PROCEDURE IF EXISTS `lp_heart_process`;
delimiter ;;
CREATE PROCEDURE `lp_heart_process`(p_date VARCHAR(10), p_hour TINYINT)
LABEL_PROC:
BEGIN 
  /*
  以下算法忽然同user多character的partner和device不同的情况
  */
  # process is done
  IF EXISTS(SELECT 1 FROM rpt_heart_log WHERE heart_date = p_date AND heart_hour = p_hour) THEN
    SELECT -1 AS return_code;
    LEAVE LABEL_PROC;
  END IF; 
  # init log
  INSERT INTO rpt_heart_log (heart_date, heart_hour, row_cnt, time_sum)
    VALUES (p_date, p_hour, 0, 0);
  
  # get start date and end date  
  SET @start_date = UNIX_TIMESTAMP(CONCAT(p_date, ' ', p_hour, ':00:00')) - 3600;
  SET @end_date = UNIX_TIMESTAMP(CONCAT(p_date, ' ', p_hour, ':59:59')) - 3600;
  
  
  # row_cnt 登录次数
  UPDATE rpt_heart_log a,
    (SELECT COUNT(*) AS login_cnt FROM or_user_login WHERE server_time BETWEEN @start_date AND @end_date) b
    SET a.row_cnt = b.login_cnt
    WHERE a.heart_date = p_date AND a.heart_hour = p_hour;
  
  
END LABEL_PROC
;;
