delimiter ;;
CREATE PROCEDURE `lp_ly_yaoyaowan_android_request`(p_game_id VARCHAR(60), p_character_id INT, p_transaction_id VARCHAR(60), p_order_id VARCHAR(60), p_amount DECIMAL(16,2), 
						p_api_money VARCHAR(100), p_api_appid VARCHAR(100), p_api_ext VARCHAR(100), p_api_uid VARCHAR(100), p_api_roleid VARCHAR(100), p_api_serverid VARCHAR(100),
						p_api_productid VARCHAR(100), p_api_coin VARCHAR(100), p_api_currency VARCHAR(100), p_api_paytype VARCHAR(100), p_api_payscheme VARCHAR(100))
LABEL_PROC:
BEGIN
	SET @pay_id = 0;
	
	# check parameter
	IF p_amount <= 0 THEN
		SELECT -1500001 AS return_code, 'amount error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	IF p_game_id = '' THEN
		SELECT -1500001 AS return_code, 'game_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	IF p_character_id <= 0 THEN
		SELECT -1500001 AS return_code, 'character_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	IF p_transaction_id = '' THEN
		SELECT -1500001 AS return_code, 'transaction_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	IF p_order_id = '' THEN
		SELECT -1500001 AS return_code, 'order_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	# select pay_ly_yaoyaowan_android from order_id
	SET @row_order = 1;
	SELECT transaction_id INTO @transaction_id FROM pay_ly_yaoyaowan_android WHERE order_id = p_order_id;
	IF FOUND_ROWS() = 0 THEN
		SET @row_order = 0;
	ELSEIF ISNULL(@transaction_id) THEN
		SET @transaction_id = '';
	END IF;	
	
	
	# select pay_ly_yaoyaowan_android from transaction_id
	SET @row_transaction = 1;
	SELECT pay_id, order_id, stat INTO @pay_id, @order_id, @stat FROM pay_ly_yaoyaowan_android WHERE transaction_id = p_transaction_id;
	IF FOUND_ROWS() = 0 THEN
		SET @row_transaction = 0;
	ELSEIF ISNULL(@order_id) THEN
		SET @order_id = '';
	END IF;
	
	
	#insert 
	IF @row_order = 0 AND @row_transaction = 0 THEN
	
		INSERT INTO pay_ly_yaoyaowan_android (game_id, character_id, transaction_id, order_id, amount, 
							api_money, api_appid, api_ext, api_uid, api_roleid, api_serverid, 
							api_productid, api_coin, api_currency, api_paytype, api_payscheme, 
							game_extend, create_date, pay_date, stat)
			VALUES (p_game_id, p_character_id, p_transaction_id, p_order_id, p_amount, 
				p_api_money, p_api_appid, p_api_ext, p_api_uid, p_api_roleid, p_api_serverid, 
				p_api_productid, p_api_coin, p_api_currency, p_api_paytype, p_api_payscheme, 
				'', NOW(), NOW(), 'N');
		SET @pay_id = @@IDENTITY; 
	#order is completed
	ELSEIF @transaction_id = p_transaction_id AND @order_id = p_order_id THEN
	
		IF UPPER(@stat) = 'S' THEN
			SELECT 1 AS return_code, 0 AS pay_id;
			LEAVE LABEL_PROC;
		END IF;	
	#check transaction_id and order_id
	ELSE
		SELECT -1500011 AS return_code, 'transaction_id or order_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	# return info
	SELECT 1 AS return_code, @pay_id AS pay_id;
END LABEL_PROC
;;
delimiter ;

