                                                p_api_receipt VARCHAR(8000), p_api_receipt_data VARCHAR(2000), p_api_type VARCHAR(100), p_api_bid VARCHAR(100))
LABEL_PROC:
BEGIN
        SET @pay_id = 0;
        IF p_amount <= 0 THEN
                SELECT -1500001 AS return_code, 'amount error' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        IF p_app_id = '' THEN
                SELECT -1500001 AS return_code, 'app_id error' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        IF p_server_id = '' THEN
                SELECT -1500001 AS return_code, 'server_id error' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        IF p_character_id <= 0 THEN
                SELECT -1500001 AS return_code, 'character_id error' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        IF p_transaction_id = '' THEN
                SELECT -1500001 AS return_code, 'transaction_id error' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        IF p_order_id = '' THEN
                SELECT -1500001 AS return_code, 'order_id error' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        IF p_product_id = '' THEN
                SELECT -1500001 AS return_code, 'product_id error' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        IF ((p_api_type <> 'sandbox') AND (p_api_type <> 'official')) THEN
                SELECT -1500001 AS return_code, 'api_type error' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        SELECT product_name, amount INTO @product_name, @amount FROM pay_app_product_info WHERE product_id = p_product_id;
        IF FOUND_ROWS() = 0 THEN
                SELECT -1500003 AS return_code, 'product not found' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        IF @amount <> p_amount THEN
                SELECT -1500004 AS return_code, 'amount error' AS return_msg;
                LEAVE LABEL_PROC;
        END IF;
        SELECT pay_id, stat INTO @pay_id, @stat FROM pay_app_ios WHERE transaction_id = p_transaction_id;
        IF FOUND_ROWS() = 0 THEN
                INSERT INTO pay_app_ios (app_id, server_id, character_id, transaction_id, order_id, product_id, amount, user_id,
                                                api_receipt, api_receipt_data, api_type, api_bid,
