                        VALUES (p_app_id, p_server_id, p_character_id, p_transaction_id, p_order_id, p_product_id, p_amount, p_user_id,
                                p_api_receipt, p_api_receipt_data, p_api_type, p_api_bid,
                                NOW(), NOW(), 'N');
                SET @pay_id = @@IDENTITY;
        ELSE
                IF UPPER(@stat) = 'S' THEN
                        SELECT 1 AS return_code, 0 AS pay_id, 0 AS amount, @product_name AS product_name;
                        LEAVE LABEL_PROC;
                END IF;
        END IF;
        SELECT 1 AS return_code, @pay_id AS pay_id, @product_name AS product_name;
