		SELECT -1500001 AS return_code, 'amount error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	IF p_server_id = '' THEN
		SELECT -1500001 AS return_code, 'server_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	IF p_character_id = '' THEN
		SELECT -1500001 AS return_code, 'character_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	IF p_transaction_id = '' THEN
		SELECT -1500001 AS return_code, 'transaction_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	IF p_order_id = '' THEN
		SELECT -1500001 AS return_code, 'order_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	
	
	# select pay_zongwan_universal from order_id
	SET @row_order = 1;
	SELECT transaction_id INTO @transaction_id FROM pay_zongwan_universal WHERE order_id = p_order_id;
	IF FOUND_ROWS() = 0 THEN
		SET @row_order = 0;
	ELSEIF ISNULL(@transaction_id) THEN
		SET @transaction_id = '';
	END IF;
	
	
	# select pay_zongwan_universal from transaction_id
	SET @row_transaction = 1;
	SELECT pay_id, order_id, stat INTO @pay_id, @order_id, @stat FROM pay_zongwan_universal WHERE transaction_id = p_transaction_id;
	IF FOUND_ROWS() = 0 THEN
		SET @row_transaction = 0;
	ELSEIF ISNULL(@order_id) THEN
		SET @order_id = '';
	END IF;
	
	
	#insert 
	IF @row_order = 0 AND @row_transaction = 0 THEN
	
		INSERT INTO pay_zongwan_universal (server_id, character_id, transaction_id, order_id, amount, product_id, 
						api_subject, api_channel, api_asyx_game_id, api_pay_order_id, api_gamezero, api_memo, api_uid, 
						create_date, pay_date, stat)
			VALUES (p_server_id, p_character_id, p_transaction_id, p_order_id, p_amount, p_product_id, 
				p_api_subject, p_api_channel, p_api_asyx_game_id, p_api_pay_order_id, p_api_gamezero, p_api_memo, p_api_uid, 
				NOW(), NOW(), 'N');
	
		SET @pay_id = @@IDENTITY; 
	#order is completed
	ELSEIF @transaction_id = p_transaction_id AND @order_id = p_order_id THEN
	
		IF UPPER(@stat) = 'S' THEN
			SELECT 1 AS return_code, 0 AS pay_id;
			LEAVE LABEL_PROC;
		END IF;	
	#check transaction_id and order_id
	ELSE
		SELECT -1500011 AS return_code, 'transaction_id or order_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	
	# return info
	SELECT 1 AS return_code, @pay_id AS pay_id;
	
END LABEL_PROC
;;
delimiter ;

