DROP PROCEDURE IF EXISTS `lp_pay_zongwan_universal_result`;
delimiter ;;
CREATE PROCEDURE `lp_pay_zongwan_universal_result`(p_pay_id INT)
LABEL_PROC:
BEGIN	
	# check parameter
	IF p_pay_id <= 0 THEN
		SELECT -1500001 AS return_code, 'pay_id error' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	SELECT stat INTO @stat FROM pay_zongwan_universal WHERE pay_id = p_pay_id;
	IF FOUND_ROWS() = 0 THEN
		SELECT -1500011 AS return_code, 'table pay_zongwan_universal row not found' AS return_msg;
		LEAVE LABEL_PROC;
	END IF;
	
	IF UPPER(@stat) = 'S' THEN
		SELECT 1 AS return_code;
		LEAVE LABEL_PROC;
	END IF;
	
	
	# update user_pay
	UPDATE pay_zongwan_universal SET 
		pay_date = NOW(), 
		stat = 'S' 
		WHERE pay_id = p_pay_id;
		
	SELECT 1 AS return_code;
	
END LABEL_PROC
;;
