-- ----------------------------
-- Procedure structure for lp_power_check
-- ----------------------------
DROP PROCEDURE IF EXISTS `lp_power_check`;
delimiter ;;
CREATE PROCEDURE `lp_power_check`(p_user_account VARCHAR(60) , p_power_code VARCHAR(60))
LABEL_PROC:
BEGIN 
  # check power id 
  SELECT power_id INTO @power_id
    FROM power_info 
    WHERE power_code = p_power_code
      AND ( state = 'A' OR state = 'P' ); 
  IF FOUND_ROWS() <= 0 THEN
    SELECT -6001005 AS return_code;
    LEAVE LABEL_PROC;       
  END IF; 
  # check user power 
  SELECT user_power INTO @user_power
    FROM user_info WHERE user_account = p_user_account;
  IF FOUND_ROWS() <= 0 THEN
    SELECT -6001006 AS return_code;
    LEAVE LABEL_PROC;       
  END IF; 
  
  IF SUBSTRING(@user_power, @power_id, 1) <> 1 THEN
    # user has no power 
    SELECT -6001007 AS return_code;
    LEAVE LABEL_PROC;
  END IF; 
  # return info
  SELECT 1 AS return_code;
  
END LABEL_PROC
;;
