delimiter ;;
CREATE PROCEDURE `lp_summary_pay_collect`(p_partner_id VARCHAR(60), p_start_time DATETIME, p_end_time DATETIME)
LABEL_PROC:
BEGIN
  # 清空表中数据
  TRUNCATE TABLE tmp_summary_pay_collect;
  TRUNCATE TABLE tmp_summary_pay_collect_pay;
  TRUNCATE TABLE tmp_summary_pay_collect_user;
  TRUNCATE TABLE tmp_summary_pay_collect_first;
  # 条件
  IF p_partner_id <> 'partner_all' AND p_partner_id <> '' THEN
    SET @s_partner_sql = CONCAT(' AND partner_id = \'', p_partner_id, '\' ');
  ELSE 
    SET @s_partner_sql = '';
  END IF;
  
  
  
  # insert tmp_summary_pay_collect_pay
  SET @s_sql = CONCAT('INSERT INTO tmp_summary_pay_collect_pay (user_account, partner_id, amount, server_time) ',
        'SELECT user_account, partner_id, money_remain, server_time ', 
          'FROM or_user_pay ', 
          'WHERE server_time BETWEEN ', UNIX_TIMESTAMP(p_start_time), ' AND ', UNIX_TIMESTAMP(p_end_time), ' ',
            @s_partner_sql); 
  #SELECT @s_sql;
  PREPARE sql_execute FROM @s_sql;
  EXECUTE sql_execute;
  #LEAVE LABEL_PROC;
  # insert tmp_summary_pay_collect_user
  INSERT IGNORE INTO tmp_summary_pay_collect_user (user_account, partner_id) 
    SELECT user_account, partner_id
      FROM tmp_summary_pay_collect_pay;
  #LEAVE LABEL_PROC;
  
  
  # insert tmp_summary_pay_collect_first
  INSERT IGNORE INTO tmp_summary_pay_collect_first (user_account, partner_id, amount) 
    SELECT b.user_account, b.partner_id, 0
      FROM (SELECT a.user_account 
          FROM or_user_pay a
          INNER JOIN tmp_summary_pay_collect_user b ON a.user_account = b.user_account 
          WHERE a.server_time < UNIX_TIMESTAMP(p_start_time)) a
      RIGHT JOIN tmp_summary_pay_collect_user b ON a.user_account = b.user_account 
      WHERE a.user_account IS NULL;
  #LEAVE LABEL_PROC;
  
  # update tmp_summary_pay_collect_first
  UPDATE tmp_summary_pay_collect_first a,
    (SELECT a.user_account, a.amount  
      FROM tmp_summary_pay_collect_pay a
      INNER JOIN tmp_summary_pay_collect_first b ON a.user_account = b.user_account 
      ORDER BY a.server_time) b
    SET a.amount = b.amount 
    WHERE a.user_account = b.user_account AND a.amount = 0;
  #LEAVE LABEL_PROC;
  # insert tmp_summary_pay_collect, login_user
  SET @s_sql = CONCAT('INSERT INTO tmp_summary_pay_collect (partner_id, login_user, pay_amount, pay_user, pay_times, first_pay_amount, first_pay_user) ',
        'SELECT partner_id, COUNT(DISTINCT user_account), 0, 0, 0, 0, 0 ', 
          'FROM or_user_login ', 
          'WHERE server_time BETWEEN ', UNIX_TIMESTAMP(p_start_time), ' AND ', UNIX_TIMESTAMP(p_end_time), ' ',
            @s_partner_sql, ' ',
          'GROUP BY partner_id'); 
  #SELECT @s_sql;
  PREPARE sql_execute FROM @s_sql;
  EXECUTE sql_execute;
  #LEAVE LABEL_PROC;
  # pay_user
  UPDATE tmp_summary_pay_collect a,
    (SELECT COUNT(*) AS pay_user, partner_id 
      FROM tmp_summary_pay_collect_user
      GROUP BY partner_id) b
    SET a.pay_user = b.pay_user
    WHERE a.partner_id = b.partner_id;
  
  
  
  
  # pay_amount, pay_times
  UPDATE tmp_summary_pay_collect a,
    (SELECT SUM(amount) AS pay_amount, COUNT(*) AS pay_times, partner_id 
      FROM tmp_summary_pay_collect_pay
      GROUP BY partner_id) b
    SET a.pay_amount = b.pay_amount, a.pay_times = b.pay_times
    WHERE a.partner_id = b.partner_id;
  
  
  # first_pay_amount, first_pay_times
  UPDATE tmp_summary_pay_collect a,
    (SELECT SUM(amount) AS first_pay_amount, COUNT(DISTINCT user_account) AS first_pay_user, partner_id 
      FROM tmp_summary_pay_collect_first 
      GROUP BY partner_id) b
    SET a.first_pay_amount = b.first_pay_amount, a.first_pay_user = b.first_pay_user
    WHERE a.partner_id = b.partner_id;
  
END LABEL_PROC
;;
delimiter ;

