CREATE PROCEDURE `lp_summary_retention_collect`(p_partner_id VARCHAR(60), p_register_start_time DATETIME, p_register_end_time DATETIME, p_login_start_time DATETIME, p_login_end_time DATETIME)
LABEL_PROC:
BEGIN
  # 清空表中数据
  TRUNCATE TABLE tmp_summary_retention_collect;  
  TRUNCATE TABLE tmp_summary_retention_collect_user;  
  
  
  
  # 组合字符串
  SET @register_date = CONCAT(DATE_FORMAT(p_register_start_time, '%Y-%m-%d'), '/', DATE_FORMAT(p_register_end_time, '%Y-%m-%d'));
  SET @login_date = CONCAT(DATE_FORMAT(p_login_start_time, '%Y-%m-%d'), '/', DATE_FORMAT(p_login_end_time, '%Y-%m-%d'));
  
  
  
  
  # 条件
  IF p_partner_id <> 'partner_all' AND p_partner_id <> '' THEN
    SET @s_partner_sql = CONCAT(' AND partner_id = \'', p_partner_id, '\' ');
  ELSE 
    SET @s_partner_sql = '';
  END IF;
  
  
  
  # insert tmp_summary_retention_collect_user
  SET @s_sql = CONCAT('INSERT IGNORE INTO tmp_summary_retention_collect_user (user_account, partner_id) ',
        'SELECT user_account, partner_id ', 
          'FROM or_user_new ', 
          'WHERE server_time BETWEEN ', UNIX_TIMESTAMP(p_register_start_time), ' AND ', UNIX_TIMESTAMP(p_register_end_time), ' ',
            @s_partner_sql); 
  #select @s_sql;
  PREPARE sql_execute FROM @s_sql;
  EXECUTE sql_execute;
  #LEAVE LABEL_PROC;
  
  
  
  
  
  
  # insert 
  INSERT INTO tmp_summary_retention_collect (partner_id, register_date, login_date, register_user, login_user, pay_user, pay_login_user)
    SELECT a.partner_id, @register_date, @login_date, COUNT(DISTINCT a.user_account), 0, COUNT(DISTINCT b.user_account), 0
      FROM tmp_summary_retention_collect_user a
      LEFT JOIN or_user_pay b ON a.user_account = b.user_account 
      GROUP BY a.partner_id;
  #LEAVE LABEL_PROC;
  # update login_user
  UPDATE tmp_summary_retention_collect a,
    (SELECT COUNT(DISTINCT a.user_account) AS login_user, b.partner_id 
      FROM or_user_login a
      INNER JOIN tmp_summary_retention_collect_user b ON a.user_account = b.user_account 
      WHERE a.server_time BETWEEN UNIX_TIMESTAMP(p_login_start_time) AND UNIX_TIMESTAMP(p_login_end_time)
      GROUP BY b.partner_id) b
    SET a.login_user = b.login_user
    WHERE a.partner_id = b.partner_id;
  #LEAVE LABEL_PROC;
  
  # update login_pay_user 充值用户在登入日期登入的
  UPDATE tmp_summary_retention_collect a,
    (SELECT COUNT(DISTINCT a.user_account) AS pay_login_user, c.partner_id
      FROM or_user_login a
      INNER JOIN or_user_pay b ON a.user_account = b.user_account 
      INNER JOIN tmp_summary_retention_collect_user c ON b.user_account = c.user_account 
      WHERE a.server_time BETWEEN UNIX_TIMESTAMP(p_login_start_time) AND UNIX_TIMESTAMP(p_login_end_time)
      GROUP BY c.partner_id) b
    SET a.pay_login_user = b.pay_login_user 
    WHERE a.partner_id = b.partner_id;
  #LEAVE LABEL_PROC;
  
  
  
  
  SELECT register_date, login_date, SUM(register_user) AS register_user, SUM(login_user) AS login_user, 
    SUM(pay_user) AS pay_user, SUM(pay_login_user) AS pay_login_user, 
    CONCAT(ROUND(IFNULL(SUM(login_user)/SUM(register_user), 0)*100, 2), '%') AS retention_ratio,
    CONCAT(ROUND(IFNULL(SUM(pay_login_user)/SUM(pay_user), 0)*100, 2), '%') AS pay_retention_ratio
    FROM tmp_summary_retention_collect;
  
  
END LABEL_PROC
;;
delimiter ;

