  DECLARE power_value VARCHAR(500);
  DECLARE cur_power CURSOR FOR SELECT g.power_value 
    FROM user_group u, group_info g
    WHERE u.group_id = g.group_id AND u.user_account = p_user_account
      AND g.state = 'A';
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;
  
  # check parameter
  IF NOT EXISTS(SELECT 1 FROM user_info WHERE user_account = p_user_account) THEN
    SELECT -2502021 AS return_code, 'user_account not found' AS return_msg;
    LEAVE LABEL_PROC;       
  END IF; 
  
  SET @user_power = p_power_value;
  SET done = 0;
  OPEN cur_power;
  FETCH cur_power INTO power_value;
  # get all power from every group
  WHILE (done = 0) DO          
    SET @idx = 1;
    SET @tmp_value = '';
    WHILE (@idx <= 500) DO
      SET @tmp_value = CONCAT(@tmp_value,(SUBSTRING(@user_power, @idx, 1) + SUBSTRING(power_value, @idx, 1)));
      
      SET @idx = @idx + 1;
    END WHILE;
    SET @user_power = REPLACE(@tmp_value, '2', '1');
    
    FETCH cur_power INTO power_value;
  END WHILE;
  CLOSE cur_power;
  
  
  # update user power
  UPDATE user_info SET power_value = p_power_value, user_power = @user_power
    WHERE user_account = p_user_account;
  
  
  
  CALL lp_common_power_set(p_user_account, '');
  
  # return info
  SELECT 1 AS return_code;
  
END LABEL_PROC
;;
delimiter ;

-- ----------------------------
-- Procedure structure for lp_user_power_list
-- ----------------------------
DROP PROCEDURE IF EXISTS `lp_user_power_list`;
delimiter ;;
CREATE PROCEDURE `lp_user_power_list`(p_user_account VARCHAR(60))
LABEL_PROC:
BEGIN 
  # check user power 
  SELECT user_power, common_power INTO @user_power, @common_power
    FROM user_info WHERE user_account = p_user_account;
  IF FOUND_ROWS() <= 0 THEN
    SELECT -6001008 AS return_code;
    LEAVE LABEL_PROC;       
  END IF; 
  
  DELETE FROM tmp_power_list WHERE user_account = p_user_account;
  
  # check all power id 
  SET @idx = 1;
  WHILE (@idx <= 500) DO
    IF SUBSTRING(@user_power, @idx, 1) = 1 THEN
      INSERT INTO tmp_power_list(user_account, class_id, power_id, class_name, parent_class_id, class_level, power_name, web_url, show_order, old_order, parent_id)
        SELECT p_user_account, p.power_class_id, p.power_id, c.class_name, c.parent_class_id, c.class_level, p.power_name, p.web_url, c.show_order*10*100*10000 + p.show_order, p.show_order, p.power_class_id 
          FROM power_class c, power_info p
          WHERE c.class_id = p.power_class_id AND p.power_id = @idx 
            AND p.state = 'A' AND c.state = 'A';
    END IF;
    
    SET @idx = @idx + 1;
  END WHILE;
  
  # insert power class
  INSERT IGNORE INTO tmp_power_list(user_account, class_id, power_id, class_name, parent_class_id, class_level, power_name, web_url, show_order, old_order, parent_id)
    SELECT p_user_account, c.class_id, 0, c.class_name, c.parent_class_id, c.class_level, '', '', c.show_order*10*100*10000, c.show_order, c.parent_class_id 
      FROM power_class c, tmp_power_list t 
      WHERE c.class_id = t.class_id AND t.user_account = p_user_account;
  
  
  
  
  # check common power 
  SET @common_menu_id = -100;
  SET @common_menu_name = '常用菜单';
  SET @idx = 1;
  WHILE (@idx <= 500) DO
    IF SUBSTRING(@common_power, @idx, 1) = 1 THEN
      INSERT INTO tmp_power_list(user_account, class_id, power_id, class_name, parent_class_id, class_level, power_name, web_url, show_order, old_order, parent_id)
        SELECT p_user_account, @common_menu_id, power_id, @common_menu_name, 0, 1, power_name, web_url, @idx, show_order, @common_menu_id
          FROM power_info 
          WHERE power_id = @idx AND state = 'A';
    END IF;
    
    SET @idx = @idx + 1;
  END WHILE;
  
  # insert common class
  INSERT INTO tmp_power_list(user_account, class_id, power_id, class_name, parent_class_id, class_level, power_name, web_url, show_order, old_order, parent_id) 
    VALUES (p_user_account, @common_menu_id, 0, @common_menu_name, 0, 1, '', '', 0, 0, 0);
    
  # insert parent class
  SELECT IFNULL(MAX(class_level), 0) INTO @class_level 
    FROM tmp_power_list WHERE user_account = p_user_account;
  WHILE (@class_level > 0) DO   
  
    INSERT IGNORE INTO tmp_power_list(user_account, class_id, power_id, class_name, parent_class_id, class_level, power_name, web_url, show_order, old_order, parent_id)
      SELECT p_user_account, c.class_id, 0, c.class_name, c.parent_class_id, c.class_level, '', '', c.show_order*10*100*10000, c.show_order, c.parent_class_id 
        FROM power_class c, tmp_power_list t 
        WHERE c.class_id = t.parent_class_id AND t.class_level = @class_level
          AND t.user_account = p_user_account;
  
    SET @class_level = @class_level - 1;
    
  END WHILE;
  
  /*
  UPDATE tmp_power_list t SET show_order=old_order*10000 + 
    ( SELECT show_order*10*100*10000 FROM power_class c WHERE t.parent_id = c.class_id )  
  WHERE power_id=0 AND class_level=2 AND user_account=p_user_account; 
  
  UPDATE tmp_power_list t SET show_order=old_order + 
    (( SELECT show_order*10000 FROM power_class c1 WHERE t.parent_id = c1.class_id ) + 
    ( SELECT c2.show_order*10*100*10000 FROM power_class c1, power_class c2 WHERE c1.parent_class_id = c2.class_id ) )
  WHERE power_id>0 AND class_level=2 AND user_account=p_user_account; 
  */
    
  SELECT user_account, class_id, power_id, class_name, parent_class_id, class_level, power_name, web_url, show_order 
    FROM tmp_power_list WHERE user_account = p_user_account 
    ORDER BY show_order;
  
  #set @c_sql = concat('SELECT power_class_id FROM tb_power WHERE power_id IN (',@power_list,') ;');
  #prepare cmd from @c_sql;
  #execute cmd;
  #DEALLOCATE PREPARE cmd;  
  
END LABEL_PROC
;;
delimiter ;

